import React, { Component } from 'react';
import { connect } from 'react-redux';
import { fetchMeetups } from '../../actions';
import { deleteMeetup } from '../../actions';

class MeetupList extends Component {

  constructor(props) {
    super(props);

    this.state = {
      meetups: []
    };

    this.handleOnAddMeetup = this.handleOnAddMeetup.bind(this);
    this.handleOnDeleteMeetup = this.handleOnDeleteMeetup.bind(this);
  }

  componentDidMount() {
    this.fetchMeetups();
  }

  handleOnDeleteMeetup(_id) {
    const confirmation = confirm('Are you sure you wish to remove meetup from list?');

    if(confirmation) {
      Meetups.deleteMeetup(_id)
      .then(() => {
        Meetups.fetchMeetups()
        .then(meetups => {
          this.setState({meetups});
        })
        .catch(error => {
          console.log(error);
          return;
        });
      })
    }
  }
  // componentDidMount() {
  //   this.props.fetchMeetups();
  // }

  // componentDidUpdate(prevProps) {
  //   if (prevProps.meetups !== this.props.meetups) {
  //     this.props.fetchMeetups();
  //   }
  // }

  renderMeetups() {
    return this.props.meetups.reverse().map(meetup => {
      return (
        <div key={meetup._id} >
          <div className="meetupContent">
            <h3 className="subject">Subject: {meetup.subject}</h3>
            <h3 className="time">Time: {meetup.time}</h3>
            <h3 className="place">Place: {meetup.place}</h3>
          </div>
          <button onClick={() => { completedButton() }} className="check-toggle" type="button">Completed</button>
        </div>
      );
    });
  }


  render() {
    return (
      <div>
        <h2>Current Scheduled Meetups</h2>
        <div className="meetupListContainer">
          {this.renderMeetups()}      
        </div>
      </div>
    );
  }
}

function completedButton() {
  console.log('completed'); 
}

function mapStateToProps({ meetups }) {
  return { meetups };
}

export default connect(mapStateToProps, { fetchMeetups })(MeetupList);
